{{ include('admin/header.html.twig') }}

{% block title %}{% endblock %}

{% block body %}
    <h1>Devis</h1>

    <table class=\"table\">
        <thead>
            <tr>
                <th>IdDevis</th>
                <th>Prix total</th>
                <th>Remise</th>
                <th>CodeDevis</th>
                <th></th>
            </tr>
        </thead>
        <tbody>
        {% for devi in devis %}
            <tr>
                <td>{{ devi.id }}</td>
                <td>{{ devi.prixTot }}</td>
                <td>{{ devi.remise }}</td>
                <td>{{ devi.codeDevis }}</td>
                <td>
                    {{ include('devis/_delete_form.html.twig') }}
                    <a href=\"{{ path('devis_edit', {'id': devi.id}) }}\">Update</a>
                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"5\">no records found</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>

    <a href=\"{{ path('devis_new') }}\">Create new</a>
{% endblock %}
{{ include('admin/footer.html.twig') }}", "devis/index.html.twig", "C:\\xampp\\htdocs\\ProjetReservation\\templates\\devis\\index.html.twig");
